import { legacy_createStore as createStore } from "redux";

const initialStateAccount = {
  balance: 0,
  loan: 0,
  loanPurpose: "",
};

const initialStateCustomer = {
  fullName: "",
  nationalID: "",
  createdAt: "",
};

function accountReducer(state = initialStateAccount, action) {
  switch (action.type) {
    case "accound/deposit":
      return {
        ...state,
        balance: state.balance + action.payload,
      };
    case "accound/withdraw":
      return {
        ...state,
        balance: state.balance - action.payload,
      };
    case "accound/requestLoan":
      if (state.loan > 0) return state;
      return {
        ...state,
        loan: action.payload.amount,
        loanPurpose: action.payload.purpose,
        balance: state.balance + action.payload.amount,
      };
    case "accound/payLoan":
      return {
        ...state,
        load: 0,
        loanPurpose: "",
        balance: state.balance - state.loan,
      };
    default:
      return state;
  }
}

function customerReducer(state = initialStateCustomer, action) {
  switch (action.type) {
    case "customer/createCustomer":
      return {
        ...state,
        fullName: action.payload.fullName,
        nationalID: action.payload.nationalID,
        createdAt: action.payload.createdAt,
      };
    default: {
      return state;
    }
  }
}

const store = createStore(accountReducer);
// store.dispatch({
//   type: "accound/requestLoan",
//   payload: { amount: 500, purpose: "Buy a Car" },
// });
// console.log(store.getState());

function deposit(amount) {
  return {
    type: "accound/deposit",
    payload: amount,
  };
}
function withdraw(amount) {
  return {
    type: "accound/withdraw",
    payload: amount,
  };
}
function requestLoan(amount, purpose) {
  return {
    type: "accound/requestLoan",
    payload: { amount: amount, purpose: purpose },
  };
}
store.dispatch(deposit(100));
console.log(store.getState());

store.dispatch(withdraw(50));
console.log(store.getState());

store.dispatch(requestLoan(500, "Buy a Car"));
console.log(store.getState());

function createCustomer(fullName, nationalID) {
  return {
    type: "customer/createCustomer",
    payload: { fullName, nationalID, createdAt: new Date().toISOString() },
  };
}

function updateName(fullName) {
  return {
    type: "customer/updateName",
    payload: fullName,
  };
}
