import { legacy_createStore as createStore } from "redux";

const initialState = {
  balance: 0,
  loan: 0,
  loanPurpose: "",
};
function reducer(state = initialState, actions) {
  switch (actions.type) {
    case "accound/deposit":
      return {
        ...state,
        balance: state.balance + actions.payload,
      };
    case "accound/withdraw":
      return {
        ...state,
        balance: state.balance - actions.payload,
      };
    case "accound/requestLoan":
      if (state.loan > 0) return state;
      return {
        ...state,
        load: actions.payload.amount,
        loanPurpose: actions.payload.amount,
      };
    case "accound/payLoan":
      return {
        ...state,
        load: 0,
        loanPurpose: "",
        balance: state.balance - state.loan,
      };
    default:
      return state;
  }
}
const store = createStore(reducer);
store.dispatch({
  type: "accound/requestLoan",
  payload: { amount: 500, purpose: "Buy a Car" },
});
console.log(store.getState());
