import Button from './Button';
import Header from './Header';
import Inputs from './Inputs';

function Post({
  name,
  email,
  password,
  setName,
  setEmail,
  setPassword,
  handleAddUser,
  status,
  updateData,
}) {
  // console.log(status);
  return (
    <div className='App'>
      <Header>API ADD</Header>
      <Inputs
        name={name}
        email={email}
        password={password}
        setName={setName}
        setEmail={setEmail}
        setPassword={setPassword}
      />
      {status !== 'update' ? (
        <Button handleClick={handleAddUser} action='form'>
          Add User
        </Button>
      ) : (
        <Button handleClick={updateData} action='form'>
          Update User
        </Button>
      )}
    </div>
  );
}

export default Post;
